                                     operation :  == ,
                                     values : [ c14plm1begla7 ]
                                },
                                {
                                     operation :  OR ,
                                     clauses : [
                                        {
                                             operation :  AND ,
                                             clauses : [
                                                {
                                                     field :  experience ,
                                                     operation :  == ,
                                                     values : [ web ]
                                                },
                                                {
                                                     field :  page_id ,
                                                     operation :  == ,
                                                     values : [ 18025 ,  47729 ]
                                                }
                                            ]
                                        },
                                        {
                                             field :  link_id ,
                                             operation :  RLIKE ,
                                             values : [ 2.* ]
                                        }
                                    ]
                                }
                            ]
                        },
                        {
                             operation :  AND ,
                             clauses : [
                                {
                                     field :  tags.player ,
                                     operation :  == ,
                                     values : [ true ]
